SET NAMES utf8mb4;
CREATE TABLE IF NOT EXISTS users (id INT AUTO_INCREMENT PRIMARY KEY, username VARCHAR(50), password VARCHAR(100), role VARCHAR(20), student_id VARCHAR(10));
CREATE TABLE IF NOT EXISTS admins (id INT AUTO_INCREMENT PRIMARY KEY, username VARCHAR(50), otp_code VARCHAR(4));
CREATE TABLE IF NOT EXISTS students (id INT AUTO_INCREMENT PRIMARY KEY, student_id VARCHAR(10), fullname VARCHAR(100), faculty VARCHAR(100), major VARCHAR(100), program VARCHAR(100), dob DATE, citizen_id VARCHAR(20), nationality VARCHAR(50), religion VARCHAR(50), blood VARCHAR(5), parent_father VARCHAR(100), parent_mother VARCHAR(100), address VARCHAR(200), gpa DECIMAL(3,2));
CREATE TABLE IF NOT EXISTS advisors (id INT AUTO_INCREMENT PRIMARY KEY, name VARCHAR(120), position VARCHAR(120), department VARCHAR(120), email VARCHAR(120), phone VARCHAR(50));
DELETE FROM users; DELETE FROM admins; DELETE FROM students; DELETE FROM advisors;
